import linecache
import sys

from PyQt5.QtWidgets import QApplication, QMainWindow, QMessageBox
import random





def call():
    def person(persons):
        persons = int(persons)
        num = random.randint(1, persons)
        data = linecache.getline('log.txt', int(num)).strip()
        ui.textBrowser.setText(data)

    def click_success():
        persons = ui.textEdit.toPlainText()
        if persons == "":
            msg_box = QMessageBox(QMessageBox.Warning, "请填写人数", '嘿，傻缺，不填人数咱点名\n\n ')
            msg_box.exec_()
            ui.textBrowser.setText("傻缺，输入人数")
        else:
            person(persons)

    def log():
        msg_box_1 = QMessageBox(QMessageBox.Warning, "未检测到log.txt文件",
                                '未检测到log.txt名单文件，在当前目录下已自动为您创建，请手动输入名字，文件内每个名字占一行\n\n ')
        msg_box_1.exec_()

    def txt():
        a = 2
        if not os.path.exists("log.txt"):
            a = 1
            with open("log.txt", "w") as f:
                print("yes")
        if a == 1:
            log()
    app = QApplication(sys.argv)
    MainWindow = QMainWindow()
    ui = Ui_MainWindow()
    ui.setupUi(MainWindow)
    MainWindow.show()
    txt()
    ui.pushButton.clicked.connect(click_success)
    sys.exit(app.exec_())

